import FormUpdate from "@/components/FormUpdate.vue";
import api from '@/services/api.ts';
export default {
  name: "users",
  props: {
    id: Number
  },
  components: {
    FormUpdate
  },
  data: () => ({
    editing: false,
    show1: false,
    show2: false,
    formData: {
      Id: 0,
      Login: "",
      Password: "",
      ConfirmPassword: "",
      Email: ""
    },
    loginRules: [v => v.length >= 4 || 'Min. 4 Caracteres', v => !!v || 'Login é Obrigatório', v => v && v.length <= 10 || 'Min. 10 Caracteres'],
    rules: {
      required: value => !!value || 'Senha é Obrigatória.',
      min: v => v.length >= 4 || 'Min. 4 Caracteres'
    },
    emailRules: [v => !!v || 'E-mail é Obrigatório.', v => /.+@.+\..+/.test(v) || 'E-mail Invalido']
  }),

  mounted() {
    if (this.id > 0) {
      this.editing = true;
      this.formData.Id = this.id;
    }
  },

  methods: {
    cleanForm() {
      this.formData.Login = "";
      this.formData.Password = "";
      this.formData.ConfirmPassword = "";
      this.formData.Email = "";
    },

    save(e) {
      e.preventDefault();
    },

    async onSave() {
      if (this.editing == true) {
        await api.put("/Users", this.formData).then(response => {
          this.msg = response.data;
        }).catch(error => {
          console.log(error.response);
        });
      } else {
        await api.post("/Users", this.formData).then(response => {
          this.msg = response.data;
        }).catch(error => {
          console.log(error.response);
        });
      }

      setTimeout(() => this.msg = "", 3000);
      this.$router.go(-1);
    }

  }
};
