import api from '@/services/api.ts';
export default {
  name: 'Signin',
  data: () => ({
    valid: true,
    formData: {
      userlogin: '',
      password: '',
      email: ''
    },
    show1: false,
    password: 'Password',
    loginRules: [v => v.length >= 4 || 'Min 4 characters', v => !!v || 'Login is required', v => v && v.length <= 10 || 'Login must be less than 10 characters'],
    rules: {
      required: value => !!value || 'Required.',
      min: v => v.length >= 4 || 'Min 4 characters'
    }
  }),
  methods: {
    cleanForm() {
      this.formData.userlogin = "";
      this.formData.password = "";
      this.formData.email = "";
    },

    async getUser() {
      console.log(this.formData);
      api.post("/Signin", this.formData).then(response => {
        console.log(response.data);
        this.$router.push('/home');
      }).catch(error => {
        console.log(error.response);
      });
      event.preventDefault();
    }

  }
